import React from "react";
import { Formik, Field, Form } from "formik";

const PostItForm = ({ addPostIt }) => (
  <div>
    <Formik
      initialValues={{
        title: "",
        details: "",
      }}
      onSubmit={(values) => {
        addPostIt({ title: values.title, description: values.description });
      }}
    >
      <Form>
        <div className="form-group">
          <label htmlFor="title">Title</label>
          <Field
            id="title"
            name="title"
            placeholder="Test"
            className="form-control"
          />
        </div>

        <div className="form-group">
          <label htmlFor="description">Description</label>
          <Field
            id="description"
            name="description"
            placeholder=""
            className="form-control"
          />
        </div>

        <button type="submit">Add Post-It</button>
      </Form>
    </Formik>
  </div>
);

export default PostItForm;
